{"changed":false,"filter":false,"title":"cliente_spec.rb","tooltip":"/ruby-intermediario-parte2/spec/cliente_spec.rb","value":"require 'spec_helper'\n\ndescribe Cliente do\n  it \"deveria ser possivel criar uma pessoa\" do\n    nome = \"Jose\"\n    sobrenome = \"da Silva\"\n    cpf = \"111.111.111-00\"\n    cliente = Cliente.new(nome, sobrenome, cpf)\n\n    expect(cliente.nome).to eq nome\n    expect(cliente.sobrenome).to eq sobrenome\n    expect(cliente.cpf).to eq cpf\n  end\n\n  it \"deveria ser possivel obter nome completo\" do\n    nome = \"Jose\"\n    sobrenome = \"da Silva\"\n    cpf = \"111.111.111-00\"\n    cliente = Cliente.new(nome, sobrenome, cpf)\n\n    expect(cliente.nome_completo).to eq \"#{cliente.nome} #{cliente.sobrenome}\"\n  end\n\n  it \"deveria ter o nome completo com tratamento, quando houver tratamento\" do\n    nome = \"Jose\"\n    sobrenome = \"da Silva\"\n    cpf = \"111.111.111-00\"\n    cliente = Cliente.new(nome, sobrenome, cpf)\n    cliente.tratamento = \"Sr.\"\n\n    expect(cliente.nome_completo).\n      to eq \"#{cliente.tratamento} #{cliente.nome} #{cliente.sobrenome}\"\n  end\nend\n","undoManager":{"mark":-1,"position":-1,"stack":[]},"ace":{"folds":[],"scrolltop":0,"scrollleft":0,"selection":{"start":{"row":0,"column":0},"end":{"row":0,"column":0},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":0},"timestamp":1570935935087}